import React from 'react';
import { useState } from 'react';
const Continets = [
  {key:1, value:"Africa"},
  {key:2, value:"Asia"},
  {key:3, value:"Europa"},
  {key:4, value:"North America"},
  {key:5, value:"south America"},
  {key:6, value:"Oceania"},
];
// import { Container } from './styles';


function UploadProductPage() {

  const [TitleValue, setTitleValue] = useState("");
  const [DescriptionValue, setDescriptionValue] = useState("");
  const [PriceValue, setPriceValue] = useState(0.00);
  const [ContinentsValue, setContinentsValue] = useState(0.00);

  const onTitleChange = event => {
    setTitleValue(event.currentTarget.value)
  }
  const onDescriptionChange = event => {
    setDescriptionValue(event.currentTarget.value)
  }
  const onPriceChange = event => {
    setPriceValue(event.currentTarget.value)
  }

  return (
    <div style={{ maxWidth: '700px', margin: '2rem auto' }}>
      <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
        <h1>Upload Product</h1>
      </div>
      <form onSubmit>
        <br />
        <br />
        <label>Title</label>
  <input onChange={onTitleChange} value={TitleValue}></input>
        <br />
        <br />
        <label>Description</label>
        <textarea onChange={onDescriptionChange} value={DescriptionValue}></textarea>
        <br />
        <br />
        <label>Price($)</label>
        <input onChange={onPriceChange} value={PriceValue} type="number"></input>
        <br />
        <br />
        <select>
          {Continets.map(item => (
            <option key={item.key} value={item.key}>{item.value} </option>
          ))}
        </select>
        <br />
        <br />
        <button onClick>
          Submit
      </button>
      </form>
    </div>
  );
}

export default UploadProductPage;
